import React, { useState, useEffect } from "react";
import { useTranslation } from "react-i18next";

// MUI Components
import FormControl from "@mui/material/FormControl";
import InputLabel from "@mui/material/InputLabel";
import Select from "@mui/material/Select";
import MenuItem from "@mui/material/MenuItem";
import Box from "@mui/material/Box";

/**
 * Base64 Images for instant loading
 */
const enFlagBase64 =
  "data:image/png;base64,";
const zhFlagBase64 =
  "data:image/png;base64,";
const esFlagBase64 =
  "data:image/png;base64,";
const deFlagBase64 =
  "data:image/png;base64,";
const frFlagBase64 =
  "data:image/png;base64,";
const hiFlagBase64 =
  "data:image/png;base64,";
const jaFlagBase64 =
  "data:image/png;base64,";

/**
 * Text-based Language Switcher
 */
const LanguageSwitcherText = () => {
  const { i18n, t } = useTranslation();
  const [currentLanguage, setCurrentLanguage] = useState(i18n.language);

  useEffect(() => {
    setCurrentLanguage(i18n.language);
  }, [i18n.language]);

  const handleLanguageChange = (event) => {
    const lang = event.target.value;
    i18n.changeLanguage(lang);
    localStorage.setItem("i18nextLng", lang);
    setCurrentLanguage(lang);
  };

  return (
    <FormControl fullWidth variant="outlined">
      <InputLabel id="language-select-label">{t("language")}</InputLabel>
      <Select
        labelId="language-select-label"
        id="language-select"
        variant="outlined"
        value={currentLanguage}
        onChange={handleLanguageChange}
        label={t("language")}
      >
        <MenuItem value="en">English</MenuItem>
        <MenuItem value="zh"></MenuItem>
        <MenuItem value="es">Espaol</MenuItem>
        <MenuItem value="de">Deutsch</MenuItem>
        <MenuItem value="hi"></MenuItem>
        <MenuItem value="fr">Franais</MenuItem>
        <MenuItem value="ja"></MenuItem>
      </Select>
    </FormControl>
  );
};

/**
 * Flag-based Language Switcher
 * Uses base64 inline images so they're always ready
 * (no network fetch required, thus preventing blank images).
 */
export const LanguageSwitcherIMG = () => {
  const { i18n } = useTranslation();
  const [currentLanguage, setCurrentLanguage] = useState(i18n.language);

  // Object mapping language codes to base64-encoded images
  const flags = {
    en: enFlagBase64,
    zh: zhFlagBase64,
    es: esFlagBase64,
    de: deFlagBase64,
    fr: frFlagBase64,
    hi: hiFlagBase64,
    ja: jaFlagBase64,
  };

  useEffect(() => {
    setCurrentLanguage(i18n.language);
  }, [i18n.language]);

  const handleLanguageChange = (event) => {
    const lang = event.target.value;
    i18n.changeLanguage(lang);
    localStorage.setItem("i18nextLng", lang);
    setCurrentLanguage(lang);
  };

  return (
    <FormControl variant="standard">
      <Select
        value={currentLanguage}
        onChange={handleLanguageChange}
        disableUnderline
        renderValue={(selectedLang) => (
          <Box display="flex" alignItems="center">
            <img
              src={flags[selectedLang]}
              alt={selectedLang}
              style={{ width: 32, height: 32 }}
            />
          </Box>
        )}
      >
        <MenuItem value="en">English</MenuItem>
        <MenuItem value="zh"></MenuItem>
        <MenuItem value="es">Espaol</MenuItem>
        <MenuItem value="de">Deutsch</MenuItem>
        <MenuItem value="hi"></MenuItem>
        <MenuItem value="fr">Franais</MenuItem>
        <MenuItem value="ja"></MenuItem>
      </Select>
    </FormControl>
  );
};

export default LanguageSwitcherText;
